package Uebungen_in_LV;

public class Uebung10_ArrayDemo1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int [] Array1 = {3,4,5};
		int [] Array2 = {7,1,3};

		System.out.println("+ "+(Array1[0]+Array2[0])+", "+(Array1[1]+Array2[1])+", "+(Array1[2]+Array2[2]));
		System.out.println("* "+(Array1[0]*Array2[0])+", "+(Array1[1]*Array2[1])+", "+(Array1[2]*Array2[2]));
	}

}
