package Uebungsbeispiele1;

public class RecheckDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int breite=2;
		int laenge=4;
		int umfang=0;
		int flaeche=0;
		
		umfang=2*breite+2*laenge;
		flaeche=breite*laenge;
		
		System.out.println("Der Umfang ist: "+umfang);
		System.out.println("Der Fläche ist: "+flaeche);
		
		

	}

}
